import { Bar } from "vue-chartjs";
import { Chart as ChartJS, Title, Tooltip, Legend, BarElement, CategoryScale, LinearScale } from "chart.js";
ChartJS.register(Title, Tooltip, Legend, BarElement, CategoryScale, LinearScale);
export default {
  name: "BarChart",
  components: {
    Bar
  },
  data() {
    return {
      chartData: {
        labels: ["January", "February", "March"],
        datasets: [{
          label: "Dataset 1",
          data: [10, 15, 30],
          backgroundColor: "#F6F1F1"
        }, {
          label: "Dataset 2",
          data: [15, 20, 40],
          backgroundColor: "#19A7CE"
        }, {
          label: "Dataset 3",
          data: [20, 25, 45],
          backgroundColor: "#146C94"
        }]
      },
      chartOptions: {
        responsive: true,
        type: "barStacked",
        // 하나로 합쳐진 스택 형태의 막대 그래프를 그립니다.
        stacked: true,
        // 스택 형태로 그립니다.
        grouped: false,
        // 하나의 막대 그래프로 그리도록 설정합니다.
        scales: {
          yAxes: [{
            ticks: {
              beginAtZero: true,
              precision: 0
            },
            position: "left"
          }]
        }
      }
    };
  }
};
